import '../assets/fonts/Mont-Regular.otf';
import './styles-new.css';
import React, { useEffect, useRef, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLocationDot, faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import logo from '../assets/pics/poatek-logo-square.png';

const format_profiles = (raw) => {
  const result = [];
  for (const [id, profile] of Object.entries(raw)) {
    profile.id = id;
    result.push(profile);
  }
  return result;
};

const Homescreen = (props) => {
  const [data, setData] = useState(null);
  const [profiles] = useState(format_profiles(props.raw_people));
  const [locationSelect, setLocationSelect] = useState('Select location');
  const [modalitySelect, setModalitySelect] = useState('Select modality');
  const [grid, setGrid] = useState([]);
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('/api/reports/office/collaboratorsByOffice?Date=2023-05-17&LocationId=1');
      const jsonData = await response.json();
      console.log('api response: ', response);
      setData(jsonData);
      console.log('api data: ', jsonData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    const filtered = profiles.filter((p) => {
      const loc = p.office === locationSelect || locationSelect === 'Select location';
      const mode = p.status === modalitySelect.toLowerCase() || modalitySelect === 'Select modality';
      return loc && mode;
    });

    const newGrid = filtered.map((profile, i) => (
      <div key={i} className='userInfo'>
        <img src={'https://picsum.photos/100?random=' + String(parseInt(profile.id))} alt='profilePic' className='userPic'></img>
        <h3 className='userName'>{profile.name}</h3>
        <p className='userTitle'>{profile.role}</p>
        <p className={profile.status === 'remote' ? 'remoteStatus' : 'onsiteStatus'}>{profile.status.toUpperCase()}</p>
      </div>
    ));

    setGrid(newGrid);
    setEmployees(filtered);
  }, [locationSelect, modalitySelect]);

  const NameSearch = () => {
    const name = document.getElementById('search').value.toLowerCase().split(' ').join('');
    const newGrid = employees
      .filter((profile) => profile.name.toLowerCase().split(' ').join('').substring(0, name.length) === name)
      .map((profile, i) => (
        <div key={i} className='userInfo'>
          <img src={'https://picsum.photos/100?random=' + String(parseInt(profile.id))} alt='profilePic' className='userPic'></img>
          <h3 className='userName'>{profile.name}</h3>
          <p className='userTitle'>{profile.role}</p>
          <p className={profile.status === 'remote' ? 'remoteStatus' : 'onsiteStatus'}>{profile.status.toUpperCase()}</p>
        </div>
      ));
    setGrid(newGrid);
  };

  const SearchEnter = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      NameSearch();
    }
  };

  const DepartmentSelect = (department) => {
    const isChecked = employees.some((profile) => profile.department === department);
    const newEmployees = isChecked
      ? employees.filter((profile) => profile.department !== department)
      : [...employees, ...profiles.filter((profile) => profile.department === department)];

    const newGrid = newEmployees.map((profile, i) => (
      <div key={i} className='userInfo'>
        <img src={'https://picsum.photos/100?random=' + String(parseInt(profile.id))} alt='profilePic' className='userPic'></img>
        <h3 className='userName'>{profile.name}</h3>
        <p className='userTitle'>{profile.role}</p>
        <p className={profile.status === 'remote' ? 'remoteStatus' : 'onsiteStatus'}>{profile.status.toUpperCase()}</p>
      </div>
    ));

    setGrid(newGrid);
    setEmployees(newEmployees);
  };

  const handleCheckboxClick = (e, department) => {
    e.stopPropagation();
    DepartmentSelect(department);
  };

  const handleDepartmentTextClick = (department) => {
    DepartmentSelect(department);
  };

  const dropdownIsActive = useRef(false);
  const activeDropdown = dropdownIsActive.current;
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        dropdownIsActive.current = false;
      }
    };

    document.addEventListener('click', handleClickOutside);
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, []);

  return (
    <div className='Homescreen'>
      {/* Rest of the component */}
    </div>
  );
};

export default Homescreen;
