import React, { useEffect, useRef, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLocationDot, faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import logo from '../assets/pics/poatek-logo-square.png';

const format_profiles = (raw) => {
  const result = [];
  for (const [id, profile] of Object.entries(raw)) {
    profile.id = id;
    result.push(profile);
  }
  return result;
};

const Homescreen = (props) => {
  const [data, setData] = useState(null);

  const [profiles] = useState(format_profiles(props.raw_people));
  // state for dropdown menus
  const [locationSelect, setLocationSelect] = useState('Select location');
  const handleLocationChange = (event) => {
    setLocationSelect(event.target.value);
  };
  const [modalitySelect, setModalitySelect] = useState('Select modality');
  const handleModalityChange = (event) => {
    setModalitySelect(event.target.value);
  };

  console.log('first profile', profiles[0]);

  const ProfileGrid = () => {
    const grid = [];
    const employees = [];

    const filtered = profiles.filter((p) => {
      const loc = p.office === locationSelect || locationSelect === 'Select location';
      const mode = p.status === modalitySelect.toLowerCase() || modalitySelect === 'Select modality';

      return loc && mode;
    });

    useEffect(() => {
      fetchData();
    }, []);

    const fetchData = async () => {
      try {
        const response = await fetch('/api/reports/office/collaboratorsByOffice?Date=2023-05-17&LocationId=1');
        const jsonData = await response.json();
        console.log('api response: ', response);
        setData(jsonData);
        console.log('api data: ', jsonData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    filtered.forEach((profile, i) => {
      // console.log(profile.status, modalitySelect);

      employees.push(profile);

      grid.push(
        <div key={i} className='userInfo'>
          {/* <div className='user'></div> */}
          <img src={`https://picsum.photos/100?random=${parseInt(profile.id)}`} alt='profilePic' className='userPic'></img>
          <h3 className='userName'>{profile.name}</h3>
          <p className='userTitle'>{profile.role}</p>
          <p className={profile.status === 'remote' ? 'remoteStatus' : 'onsiteStatus'}>
            {profile.status.toUpperCase()}
          </p>
        </div>
      );
    });

    return grid;
  };

  // Set initial grid to all employees.
  const [grid, setGrid] = useState(ProfileGrid());

  // Current profile data.
  const [employees, setEmployees] = useState(profiles);

  const NameSearch = () => {
    const grid = [];

    const searchProfile = (e) => {
      const searchString = e.target.value.toLowerCase();

      const filteredProfiles = employees.filter((profile) =>
        profile.name.toLowerCase().includes(searchString)
      );

      filteredProfiles.forEach((profile, i) => {
        grid.push(
          <div key={i} className='userInfo'>
            {/* <div className='user'></div> */}
            <img
              src={`https://picsum.photos/100?random=${parseInt(profile.id)}`}
              alt='profilePic'
              className='userPic'></img>
            <h3 className='userName'>{profile.name}</h3>
            <p className='userTitle'>{profile.role}</p>
            <p className={profile.status === 'remote' ? 'remoteStatus' : 'onsiteStatus'}>
              {profile.status.toUpperCase()}
            </p>
          </div>
        );
      });

      setGrid(grid);
    };

    return (
      <div className='searchContainer'>
        <FontAwesomeIcon icon={faMagnifyingGlass} className='searchIcon' />
        <input
          type='text'
          className='search'
          placeholder='Search by name'
          onChange={(e) => searchProfile(e)}
          onKeyDown={(e) => SearchEnter(e)}
        />
      </div>
    );
  };

  const SearchEnter = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
    }
  };

  const DepartmentSelect = () => {
    const [dropdownIsActive, setDropdownIsActive] = useState(false);
    const [activeDropdown, setActiveDropdown] = useState('');

    const dropdownRef = useRef(null);

    useEffect(() => {
      const handleClickOutside = (event) => {
        if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
          setDropdownIsActive(false);
        }
      };

      document.addEventListener('mousedown', handleClickOutside);

      return () => {
        document.removeEventListener('mousedown', handleClickOutside);
      };
    }, []);

    const handleCheckboxClick = (event, department) => {
      event.stopPropagation();

      if (department === activeDropdown) {
        setDropdownIsActive(false);
        setActiveDropdown('');
      } else {
        setDropdownIsActive(true);
        setActiveDropdown(department);
      }
    };

    const handleDepartmentTextClick = (department) => {
      if (department === activeDropdown) {
        setDropdownIsActive(false);
        setActiveDropdown('');
      } else {
        setDropdownIsActive(true);
        setActiveDropdown(department);
      }
    };

    const departments = ['All', 'Department1', 'Department2', 'Department3'];

    const handleDepartmentChange = (department) => {
      const filteredProfiles = department === 'All' ? profiles : profiles.filter((profile) => profile.department === department);

      setEmployees(filteredProfiles);

      const filteredGrid = filteredProfiles.map((profile) => (
        <div key={profile.id} className='userInfo'>
          {/* <div className='user'></div> */}
          <img
            src={`https://picsum.photos/100?random=${parseInt(profile.id)}`}
            alt='profilePic'
            className='userPic'></img>
          <h3 className='userName'>{profile.name}</h3>
          <p className='userTitle'>{profile.role}</p>
          <p className={profile.status === 'remote' ? 'remoteStatus' : 'onsiteStatus'}>
            {profile.status.toUpperCase()}
          </p>
        </div>
      ));

      setGrid(filteredGrid);
    };

    return (
      <div className='dropdownContainer'>
        <button
          className='dropdownButton'
          onClick={() => setDropdownIsActive(!dropdownIsActive)}
          ref={dropdownRef}>
          <span>{activeDropdown || 'All'}</span>
          <FontAwesomeIcon icon={faLocationDot} className='dropdownIcon' />
        </button>
        {dropdownIsActive && (
          <ul className='dropdownList'>
            {departments.map((department) => (
              <li
                key={department}
                className={`dropdownItem ${activeDropdown === department ? 'active' : ''}`}
                onClick={() => handleDepartmentChange(department)}>
                <input
                  type='checkbox'
                  className='dropdownCheckbox'
                  checked={activeDropdown === department}
                  onChange={(event) => handleCheckboxClick(event, department)}
                />
                <span className='dropdownText' onClick={() => handleDepartmentTextClick(department)}>
                  {department}
                </span>
              </li>
            ))}
          </ul>
        )}
      </div>
    );
  };

  return (
    <div className='homescreenContainer'>
      <div className='header'>
        <img src={logo} alt='logo' className='logo' />
        <h1 className='title'>Employee Directory</h1>
      </div>
      <div className='searchBar'>
        <NameSearch />
        <DepartmentSelect />
      </div>
      <div className='profilesContainer'>{grid}</div>
    </div>
  );
};

export default Homescreen;
